INSERT INTO lupa (id, edellinen_lupa_id, paatoskierros_id, lupatila_id, asiatyyppi_id, diaarinumero, jarjestaja_ytunnus, jarjestaja_oid, alkupvm, loppupvm, paatospvm, meta, maksu, luoja, luontipvm, paivittaja, paivityspvm) VALUES (13, NULL, 19, 3, 2, '143/531/2017', '0116936-9', '1.2.246.562.10.33795425587', '2016-12-15', NULL, '2016-12-07', '{"kirje": "Opetus- ja kulttuuriministeriö lähettää koulutuksen järjestäjille ehdotukset uusiksi ammatillisten tutkintojen ja koulutuksen järjestämisluviksi, kuten hallituksen esityksessä laiksi ammatillisesta koulutuksesta ja eräiksi siihen liittyviksi laeiksi (HE 39/2017 vp) on esitetty. <br/><br/>Ehdotukset on laadittu hallituksen esityksen siirtymäsäännösten (133 §, 134 §) mukaisesti vastaamaan voimassa olevia järjestämislupia ja tutkinnon järjestämissopimuksia ottaen huomioon järjestämislupasäätelyssä tapahtuvat sisällölliset muutokset. Ehdotusten valmistelussa noudatetut periaatteet on kuvattu yksityiskohtaisesti muistiossa ja muussa tausta-aineistossa, joka tulee ministeriön verkkosivuille.  Muistio liitteineen, hakemuslomakkeet ja niihin liittyvät ohjeistukset sekä muu mahdollinen aineisto toimitetaan kaikilta osin viimeistään 19.6.2017 mennessä ammatillisen koulutuksen reformin sivuille osoitteeseen http://minedu.fi/jarjestamisluvat-ja-muu-ohjaus <br/><br/> Opetus- ja kulttuuriministeriön valmistelemat järjestämislupaehdotukset eivät sisällä työvoimakoulutuksen tehtävää, laajennettua oppisopimuskoulutuksen tehtävää eikä vankilaopetuksen tehtävää. Nämä tehtävät ovat kaikkien koulutuksen järjestäjien haettavissa.<br/><br/> Ministeriö pyytää 11.8.2017 mennessä koulutuksen järjestäjää toimittamaan vastineensa oheiseen lupaehdotukseen. Mikäli koulutuksen järjestäjä hakee edellä mainittuja uusia tehtäviä tai muutosta ministeriön valmistelemaan lupaehdotukseen, hakemukset tulee sisällyttää vastineeseen. Mikäli koulutuksen järjestäjät ovat yhdistymässä 1.1.2018 lukien, fuusiota koskevat erilliset hakemukset liitteineen tulee toimittaa samassa aikataulussa. <br/><br/>Uuden lainsäädännön mukaiset järjestämisluvat tullaan myöntämään pikimmiten kun laki ammatillisesta koulutuksesta on vahvistettu, kuitenkin viimeistään syyskuun 2017 loppuun mennessä. Mikäli koulutuksen järjestäjä ei ole määräajassa antanut vastinetta ministeriön tekemään ehdotukseen, lupa päätetään lähtökohtaisesti ministeriön tekemän ehdotuksen pohjalta. <br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>Ministeriö tulee niiltä osin kuin se on mahdollista ratkaisemaan samassa yhteydessä myös vireillä olevat järjestäjien koulutustehtävän muuttamista koskevat hakemukset. Mikäli koulutuksen järjestäjällä on vireillä olevia hakemuksia, joiden se katsoo vielä olevan ajankohtaisia, järjestäjän tulee täydentää vastineessaan ne vastaamaan hallituksen esityksen mukaista lainsäädäntöä. Mikäli aikaisempi hakemus ei ole enää tarpeellinen, järjestäjää pyydetään peruuttamaan se vastineessaan.", "ministeri": "Sanni Grahn-Laasonen", "esittelija": "Tarja Koskimäki", "paatos_old_id": "1000458", "ministeri_nimike": "Opetusministeri", "esittelija_nimike": "Ylitarkastaja", "koulutustehtava_old_id": "1000563"}', false, 'oiva', '2017-04-26 13:49:05.83453', NULL, NULL);
