INSERT INTO lupa (id, edellinen_lupa_id, paatoskierros_id, lupatila_id, asiatyyppi_id, diaarinumero, jarjestaja_ytunnus, jarjestaja_oid, alkupvm, loppupvm, paatospvm, meta, maksu, luoja, luontipvm, paivittaja, paivityspvm) VALUES (68, NULL, 19, 3, 2, '76/531/2017', '0209492-8', '1.2.246.562.10.72401400774', '2016-12-15', NULL, '2016-12-07', '{"kirje": "Undervisnings- och kulturministeriet sänder utbildningsanordnarna förslag till nya anordnartillstånd för yrkesinriktade examina och yrkesutbildning i enlighet med regeringens proposition med förslag till lag om yrkesutbildning och till vissa lagar som har samband med den (RP 39/2017).<br/><br/>Förslagen har utarbetats med beaktande av övergångsbestämmelserna i regeringens proposition (133 §, 134 §) så att de motsvarar de gällande tillstånden för utbildning och examina och med beaktande av de innehållsmässiga ändringarna i tillståndsregleringen. Principerna som följts i beredningen av förslagen har beskrivits i detalj i promemorian och det övriga bakgrundsmaterial som publiceras på ministeriets webbplats.  Promemorian med bilagor, ansökningsblanketter och anvisningar för dessa samt eventuellt annat material läggs ut på webbplatsen för yrkesutbildningsreformen senast den 19 juni 2017: http://minedu.fi/jarjestamisluvat-ja-muu-ohjaus<br/><br/>Förslagen till anordnartillstånd som utarbetats av undervisnings- och kulturministeriet innefattar inte uppgiften som gäller arbetskraftspolitisk utbildning, den utvidgade uppgiften som gäller läroavtalsutbildning och inte heller uppgiften som gäller fängelseundervisning. Dessa uppgifter kan ansökas av alla utbildningsanordnare. <br/><br/>Ministeriet ber utbildningsanordnaren sända ett svar på bifogade förslag senast den 11 augusti 2017. Om utbildningsanordnaren ansöker om ovannämnda nya uppgifter eller om ändringar i ministeriets förslag ska ansökan ingå i svaret. Om utbildningsanordnarna ska sammanslås från den 1 januari 2018 ska separata ansökningar om fusionen inklusive bilagor sändas inom samma tidtabell.<br/><br/>Anordnartillstånden enligt den nya lagstiftningen kommer att beviljas snarast möjligt efter att den nya lagen om yrkesutbildning stadfästs, dock senast före utgången av september 2017. Om utbildningsanordnaren inte inom utsatt tid skickat ett svar på ministeriets förslag kommer tillståndet att avgöras på basis av förslaget.<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>I samband med detta kommer ministeriet även i mån av möjlighet att avgöra anordnarnas aktuella ansökningar om att ändra utbildningsuppgiften. Om utbildningsanordnaren har inlämnade ansökningar som ännu anses vara aktuella ska anordnaren komplettera dem i sitt svar så att de motsvarar lagstiftningen enligt regeringens proposition. Om den tidigare ansökan inte längre är nödvändig ombeds anordnaren återkalla den i sitt svar.", "ministeri": "Sanni Grahn-Laasonen", "esittelija": "Mari Pastila-Eklund", "paatos_old_id": "1000515", "ministeri_nimike": "Opetusministeri", "esittelija_nimike": "Undervisningsråd", "koulutustehtava_old_id": "1000620"}', false, 'oiva', '2017-04-26 13:49:05.83453', NULL, NULL);
