INSERT INTO lupa (id, edellinen_lupa_id, paatoskierros_id, lupatila_id, asiatyyppi_id, diaarinumero, jarjestaja_ytunnus, jarjestaja_oid, alkupvm, loppupvm, paatospvm, meta, maksu, luoja, luontipvm, paivittaja, paivityspvm) VALUES (128, NULL, 19, 3, 2, '125/531/2017', '2334857-9', '1.2.246.562.10.453646549010', '2016-12-15', NULL, '2016-12-07', '{"kirje": "Undervisnings- och kulturministeriet sänder utbildningsanordnarna förslag till nya anordnartillstånd för yrkesinriktade examina och yrkesutbildning i enlighet med regeringens proposition med förslag till lag om yrkesutbildning och till vissa lagar som har samband med den (RP 39/2017).<br/><br/>Förslagen har utarbetats med beaktande av övergångsbestämmelserna i regeringens proposition (133 §, 134 §) så att de motsvarar de gällande tillstånden för utbildning och examina och med beaktande av de innehållsmässiga ändringarna i tillståndsregleringen. Principerna som följts i beredningen av förslagen har beskrivits i detalj i promemorian och det övriga bakgrundsmaterial som publiceras på ministeriets webbplats.  Promemorian med bilagor, ansökningsblanketter och anvisningar för dessa samt eventuellt annat material läggs ut på webbplatsen för yrkesutbildningsreformen senast den 19 juni 2017: http://minedu.fi/jarjestamisluvat-ja-muu-ohjaus<br/><br/>Förslagen till anordnartillstånd som utarbetats av undervisnings- och kulturministeriet innefattar inte uppgiften som gäller arbetskraftspolitisk utbildning, den utvidgade uppgiften som gäller läroavtalsutbildning och inte heller uppgiften som gäller fängelseundervisning. Dessa uppgifter kan ansökas av alla utbildningsanordnare. <br/><br/>Ministeriet ber utbildningsanordnaren sända ett svar på bifogade förslag senast den 11 augusti 2017. Om utbildningsanordnaren ansöker om ovannämnda nya uppgifter eller om ändringar i ministeriets förslag ska ansökan ingå i svaret. Om utbildningsanordnarna ska sammanslås från den 1 januari 2018 ska separata ansökningar om fusionen inklusive bilagor sändas inom samma tidtabell.<br/><br/>Anordnartillstånden enligt den nya lagstiftningen kommer att beviljas snarast möjligt efter att den nya lagen om yrkesutbildning stadfästs, dock senast före utgången av september 2017. Om utbildningsanordnaren inte inom utsatt tid skickat ett svar på ministeriets förslag kommer tillståndet att avgöras på basis av förslaget.<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>I samband med detta kommer ministeriet även i mån av möjlighet att avgöra anordnarnas aktuella ansökningar om att ändra utbildningsuppgiften. Om utbildningsanordnaren har inlämnade ansökningar som ännu anses vara aktuella ska anordnaren komplettera dem i sitt svar så att de motsvarar lagstiftningen enligt regeringens proposition. Om den tidigare ansökan inte längre är nödvändig ombeds anordnaren återkalla den i sitt svar.", "ministeri": "Sanni Grahn-Laasonen", "esittelija": "Tarja Koskimäki", "paatos_old_id": "1000531", "ministeri_nimike": "Opetusministeri", "esittelija_nimike": "Överinspektör", "koulutustehtava_old_id": "1000636"}', false, 'oiva', '2017-04-26 13:49:05.83453', NULL, NULL);
INSERT INTO lupa (id, edellinen_lupa_id, paatoskierros_id, lupatila_id, asiatyyppi_id, diaarinumero, jarjestaja_ytunnus, jarjestaja_oid, alkupvm, loppupvm, paatospvm, meta, maksu, luoja, luontipvm, paivittaja, paivityspvm) VALUES (3, NULL, 19, 1, 1, '128/531/2017', '0201472-1', '1.2.246.562.10.70829532053', '2018-01-01', NULL, '2017-08-01', '{"kirje": "Opetus- ja kulttuuriministeriö lähettää koulutuksen järjestäjille ehdotukset uusiksi ammatillisten tutkintojen ja koulutuksen järjestämisluviksi, kuten hallituksen esityksessä laiksi ammatillisesta koulutuksesta ja eräiksi siihen liittyviksi laeiksi (HE 39/2017 vp) on esitetty. <br/><br/>Ehdotukset on laadittu hallituksen esityksen siirtymäsäännösten (133 §, 134 §) mukaisesti vastaamaan voimassa olevia järjestämislupia ja tutkinnon järjestämissopimuksia ottaen huomioon järjestämislupasäätelyssä tapahtuvat sisällölliset muutokset. Ehdotusten valmistelussa noudatetut periaatteet on kuvattu yksityiskohtaisesti muistiossa ja muussa tausta-aineistossa, joka tulee ministeriön verkkosivuille.  Muistio liitteineen, hakemuslomakkeet ja niihin liittyvät ohjeistukset sekä muu mahdollinen aineisto toimitetaan kaikilta osin viimeistään 19.6.2017 mennessä ammatillisen koulutuksen reformin sivuille osoitteeseen http://minedu.fi/jarjestamisluvat-ja-muu-ohjaus <br/><br/> Opetus- ja kulttuuriministeriön valmistelemat järjestämislupaehdotukset eivät sisällä työvoimakoulutuksen tehtävää, laajennettua oppisopimuskoulutuksen tehtävää eikä vankilaopetuksen tehtävää. Nämä tehtävät ovat kaikkien koulutuksen järjestäjien haettavissa.<br/><br/> Ministeriö pyytää 11.8.2017 mennessä koulutuksen järjestäjää toimittamaan vastineensa oheiseen lupaehdotukseen. Mikäli koulutuksen järjestäjä hakee edellä mainittuja uusia tehtäviä tai muutosta ministeriön valmistelemaan lupaehdotukseen, hakemukset tulee sisällyttää vastineeseen. Mikäli koulutuksen järjestäjät ovat yhdistymässä 1.1.2018 lukien, fuusiota koskevat erilliset hakemukset liitteineen tulee toimittaa samassa aikataulussa. <br/><br/>Uuden lainsäädännön mukaiset järjestämisluvat tullaan myöntämään pikimmiten kun laki ammatillisesta koulutuksesta on vahvistettu, kuitenkin viimeistään syyskuun 2017 loppuun mennessä. Mikäli koulutuksen järjestäjä ei ole määräajassa antanut vastinetta ministeriön tekemään ehdotukseen, lupa päätetään lähtökohtaisesti ministeriön tekemän ehdotuksen pohjalta. <br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>Ministeriö tulee niiltä osin kuin se on mahdollista ratkaisemaan samassa yhteydessä myös vireillä olevat järjestäjien koulutustehtävän muuttamista koskevat hakemukset. Mikäli koulutuksen järjestäjällä on vireillä olevia hakemuksia, joiden se katsoo vielä olevan ajankohtaisia, järjestäjän tulee täydentää vastineessaan ne vastaamaan hallituksen esityksen mukaista lainsäädäntöä. Mikäli aikaisempi hakemus ei ole enää tarpeellinen, järjestäjää pyydetään peruuttamaan se vastineessaan.", "ministeri": "Sanni Grahn-Laasonen", "esittelija": "Tarja Koskimäki", "ministeri_nimike": "Opetusministeri", "esittelija_nimike": "Ylitarkastaja"}', false, 'oiva', '2017-04-20 11:29:41.032245', NULL, NULL);
